import { Injectable } from '@angular/core';
import { Receta } from 'src/app/pages/domain/receta';

@Injectable({
  providedIn: 'root'
})
export class RecetasService {
  private recetas: Receta[] = [];

  selectedImage: File | null = null;

  agregarReceta(receta: Receta, imagenFile: File | null) {
    const recetas = this.obtenerRecetas();

    if (imagenFile) {
      const reader = new FileReader();

      // Agregar una verificación para asegurarse de que event y event.target no sean nulos
      reader.onload = (event) => {
        if (event && event.target) {
          const imagenBase64 = event.target.result as string;

          // Guardar la cadena de la imagen en la receta
          receta.imagen = imagenBase64; // Ahora estamos guardando la cadena en la propiedad imagen

          recetas.push(receta);
          localStorage.setItem('recetas', JSON.stringify(recetas));
        }
      };

      // Leer la imagen y activar el evento onload
      reader.readAsDataURL(imagenFile);
    } else {
      recetas.push(receta);
      localStorage.setItem('recetas', JSON.stringify(recetas));
    }
  }

  obtenerRecetas() {
    const recetas = localStorage.getItem('recetas');
    return recetas ? JSON.parse(recetas) : [];
  }

  borrarRecetas() {
    localStorage.removeItem('recetas');
  }

  buscarRecetasPorNombre(termino: string): Receta[] {
    termino = termino.toLowerCase(); // Convierte el término de búsqueda a minúsculas para hacer la búsqueda sin distinción entre mayúsculas y minúsculas

    // Filtra las recetas que coincidan con el término de búsqueda en el nombre
    return this.recetas.filter(receta => receta.nombre.toLowerCase().includes(termino));
  }

}
