import { Injectable } from '@angular/core';
import { Tarea } from 'src/app/pages/domain/tarea';

@Injectable({
  providedIn: 'root'
})
export class RecetasService {
  private tareas: Tarea[] = [];

  selectedImage: File | null = null;

  agregarReceta(receta: Receta) {
    const tareas = this.obtenerRecetas();
      // Agregar una verificación para asegurarse de que event y event.target no sean nulos
          tareas.push(receta);
          localStorage.setItem('recetas', JSON.stringify(tareas));
  }

  obtenerRecetas() {
    const tareas = localStorage.getItem('recetas');
    return recetas ? JSON.parse(recetas) : [];
  }

  borrarRecetas() {
    localStorage.removeItem('recetas');
  }

}
