import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { RecetasService } from 'src/app/recetas.service';
import { Receta } from '../domain/receta';



@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.css']
})
export class FormularioComponent {

  formulario: FormGroup;

  constructor(private fb: FormBuilder, private recetasService: RecetasService) {
    this.formulario = this.fb.group({
      nombre: ['', Validators.required],
      ingredientes: ['', Validators.required],
      pasos: ['', Validators.required]
    });
  }

  onSubmit() {
    if (this.formulario.valid) {
      const nuevaReceta: Receta = this.formulario.value;
      this.recetasService.agregarReceta(nuevaReceta);
      this.formulario.reset();
    }
  }
}
<div class="form-container">
    <form [formGroup]="formulario" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label for="nombreReceta">Nombre de la Receta:</label>
          <input type="text" id="nombreReceta" formControlName="nombre" required>
        </div>

        <div class="form-group">
          <label for="ingredientes">Ingredientes:</label>
          <textarea id="ingredientes" formControlName="ingredientes" required></textarea>
        </div>

        <div class="form-group">
          <label for="pasos">Pasos:</label>
          <textarea id="pasos" formControlName="pasos" required></textarea>
        </div>

        <button type="submit" [disabled]="formulario.invalid">Agregar Receta</button>
      </form>

</div>
