import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-formulario',
  templateUrl: './formulario.component.html',
  styleUrls: ['./formulario.component.css']
})
export class FormularioComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
    const recetas = JSON.parse(localStorage.getItem('recipes')) || [];

    document.getElementById('recetaForm')?.addEventListener('submit', (e) => {
      e.preventDefault();

      const nombreReceta = (document.getElementById('nombreReceta') as HTMLInputElement).value;
      const ingredientes = (document.getElementById('ingredientes') as HTMLInputElement).value;
      const pasos = (document.getElementById('pasos') as HTMLInputElement).value;

      const imagenInput = document.getElementById('imagen') as HTMLInputElement;
      const imagen = imagenInput?.files ? imagenInput?.files[0] : null;

      if (imagen) {
        // Display image information in the console
        console.log('Imagen Seleccionada:');
        console.log('Nombre: ', imagen.name);
        console.log('Tipo: ', imagen.type);
        console.log('Tamaño: ', imagen.size);

        const receta = {
          nombreReceta: nombreReceta,
          ingredientes: ingredientes,
          pasos: pasos,
          imagen: imagen, // Store the image in the recipe object
        };

        if (window.confirm('Receta enviada. ¿Deseas enviar otra receta?')) {
          recetas.push(receta);

          // Muestra todas las recetas en la consola
          console.log('Todas las recetas:');
          recetas.forEach(function (receta, index) {
            console.log('Receta #' + (index + 1));
            console.log('Nombre de la Receta:', receta.nombreReceta);
            console.log('Ingredientes:', receta.ingredientes);
            console.log('Pasos:', receta.pasos);
            console.log('Imagen:', receta.imagen.name); // Display the image name
          });

          localStorage.setItem('recipes', JSON.stringify(recetas));

          // Limpia el formulario
          (document.getElementById('recetaForm') as HTMLFormElement)?.reset();
        } else {
          // Handle user cancellation or redirection
        }
      } else {
        // Handle case when no image is selected
        console.log('No se seleccionó ninguna imagen.');
      }
    });
  }
}
