import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { LeerService } from 'src/app/leer.service';
import { RecetasFirebaseService } from 'src/app/services/recetas-firebase.service';
import { Tarea } from '../domain/tarea';



@Component({
  selector: 'app-crear',
  templateUrl: './crear.component.html',
  styleUrls: ['./crear.component.css']
})
export class CrearComponent {

  formulario: FormGroup;

  constructor(private fb: FormBuilder, private leerService: LeerService, private recetasFirebaseService: RecetasFirebaseService) {
    this.formulario = this.fb.group({
      nota: ['', Validators.required],
      fecha: ['', Validators.required],
      etiqueta: ['', Validators.required],
    });
  }

  onSubmit() {
    if (this.formulario.valid) {
      const tarea = this.formulario.value as Tarea;
      this.leerService.agregarTarea(tarea);
      this.recetasFirebaseService.save(tarea);
      this.formulario.reset();
      }
    }
}

    <form [formGroup]="formulario" (ngSubmit)="onSubmit()">
        <div class="form-group">
          <label for="nota">Nota:</label>
          <input type="text" id="nota" formControlName="nota" required>
        </div>

        <div class="form-group">
          <label for="fecha">Fecha:</label>
          <textarea id="fecha" formControlName="fecha" required></textarea>
        </div>

        <div class="form-group">
          <label for="etiqueta">Etiqueta:</label>
          <textarea id="etiqueta" formControlName="etiqueta" required></textarea>
        </div>
        <button type="submit" [disabled]="formulario.invalid">Agregar Receta</button>
      </form>
